    function normalizeResp(r) { const d = r.data || {}; const bills = Array.isArray(d.bills) ? d.bills : []; const curr = +d.statement_closing || +bills[0]?.amount || 0; const prev = +bills[1]?.amount || 0; const tot = +d.total_bill_amount || curr; return { key: `${r.product_id}::${d.account||r.account}`, account: d.account || r.account, name: d.name || '-', address: d.address || '-', curr: String(curr), prev: String(prev), total: String(tot), nhap: '', xuat: '', memberName: '', employee_username: '', amount_current: String(curr), amount_previous: String(prev), provider_id: r.product_id, raw: d }; }

    // ** SỬA: Cập nhật hàm normalizeResp_Port2 **
    function normalizeResp_Port2(r, accountCode) {
        const provider_id = 'C2-7ty.vn';

        // TRƯỜNG HỢP 1: API 7ty.vn trả về THÀNH CÔNG (status 200)
        // Cấu trúc data: r.success == true, r.data.success == true
        if (r.success && r.data && r.data.success && r.data.data && r.data.data.bills) {
            if (r.data.data.bills.length > 0) {
                const bill = r.data.data.bills[0];
                const name = bill.customerName || '-';
                const address = bill.address || '-';
                const total = bill.moneyAmount || '0';
                return {
                    key: `${provider_id}::${accountCode}`, account: accountCode,
                    name: name, address: address,
                    curr: String(total), prev: '0', total: String(total),
                    nhap: '', xuat: '', memberName: '', employee_username: '',
                    amount_current: String(total), amount_previous: '0',
                    provider_id: provider_id, raw: r.data.data // Lưu data.data
                };
            } else {
                // Thành công nhưng không có bill (không nợ cước)
                return {
                    key: `${provider_id}::${accountCode}`, account: accountCode,
                    name: `(Mã ${accountCode})`, address: 'Không nợ cước',
                    curr: '0', prev: '0', total: '0',
                    nhap: '', xuat: '', memberName: '', employee_username: '',
                    amount_current: '0', amount_previous: '0',
                    provider_id: provider_id, raw: r.data.data
                };
            }
        }


        // TRƯỜNG HỢP 2: API 7ty.vn trả về lỗi
        let errorMsg = r.error?.message || r.details || 'Lỗi tra cứu C2';
        if (r.data && r.data.error) {
            errorMsg = r.data.error.message || 'Lỗi không xác định C2';
        }
        if (r.error && (r.error.code === 'PAYBILL_QUERY_ERROR-01' || r.error === 'Khách hàng không nợ cước')) {
             errorMsg = "Không nợ cước";
        }
        if (r.detail && Array.isArray(r.detail) && r.detail[0]?.msg) {
            errorMsg = `Lỗi Cổng 2: ${r.detail[0].msg.replace("Field required", "Trường bắt buộc")} (trường ${r.detail[0].loc.join('.')})`;
        }




        return {
            key: `${provider_id}::${accountCode}`, account: accountCode,
            name: `(Mã ${accountCode})`, address: errorMsg,
            curr: '0', prev: '0', total: '0',
            nhap: '', xuat: '', memberName: '', employee_username: '',
            amount_current: '0', amount_previous: '0',
            provider_id: provider_id, raw: { error: errorMsg }
        };
    }




