    <script src="app.js" defer></script>
    <script>
      (() => {
        'use strict'
        const THEME_KEY = 'bs-theme';
        const getStoredTheme = () => localStorage.getItem(THEME_KEY);
        const setStoredTheme = theme => localStorage.setItem(THEME_KEY, theme);
        const getPreferredTheme = () => {
          const storedTheme = getStoredTheme();
          if (storedTheme) return storedTheme;
          return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
        }
        const setTheme = theme => {
          if (theme === 'auto') {
            theme = window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
          }
          document.documentElement.setAttribute('data-bs-theme', theme);
        }
        setTheme(getPreferredTheme());
        window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', () => {
          if (getStoredTheme() === 'auto') setTheme('auto');
        });
        window.addEventListener('DOMContentLoaded', () => {
          document.querySelectorAll('[data-bs-theme-value]').forEach(toggle => {
            toggle.addEventListener('click', (e) => {
              e.preventDefault();
              const theme = e.currentTarget.getAttribute('data-bs-theme-value');
              setStoredTheme(theme);
              setTheme(theme);
              document.querySelectorAll('[data-bs-theme-value].active').forEach(el => el.classList.remove('active'));
              e.currentTarget.classList.add('active');
            });
          });
        });
      })()
    </script>
