app.get('/api/members', isAuthenticated, async (_req, res) => { try { const result = await db.query('SELECT * FROM members ORDER BY name'); res.json(result.rows); } catch (err) { console.error('Lỗi /api/members (GET):', err); res.status(500).json({ error: err.message }); } });
