        employeePhoneInput = $('employeePhone'); employeeAddressInput = $('employeeAddress');
        employeeAvatarUrlInput = $('employeeAvatarUrl'); employeeCompanyNameInput = $('employeeCompanyName');
        employeeTaxCodeInput = $('employeeTaxCode'); employeeError = $('employeeError');
        employeeSubmitBtn = employeeForm.querySelector('button[type="submit"]');
        notesModalEl = $('notesModal'); notesModal = new bootstrap.Modal(notesModalEl);
        notesEmployeeNameSpan = $('notesEmployeeName'); notesListUl = $('notesList');
        addNoteForm = $('addNoteForm'); noteEmployeeIdInput = $('noteEmployeeId');
        newNoteTextInput = $('newNoteText'); addNoteSubmitBtn = addNoteForm.querySelector('button[type="submit"]');


        addEventListeners(); // Gán sự kiện sau khi lấy elements
    }




    // --- Authentication ---
    loginForm.onsubmit = async (e) => {
