        if (addNoteForm) { addNoteForm.onsubmit = async (e) => { e.preventDefault(); const employeeId = noteEmployeeIdInput.value; const noteText = newNoteTextInput.value.trim(); if (!employeeId || !noteText) return; addNoteForm.querySelector('.alert')?.remove(); setButtonLoading(addNoteSubmitBtn, true); try { const resp = await fetch(`/api/employees/${employeeId}/notes`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ note_text: noteText }), credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); const li = document.createElement('li'); li.className = 'list-group-item'; li.innerHTML = ` <p class="mb-1">${result.note_text}</p> <small class="note-meta">Bởi: ${result.author_username || currentUser.username} - Lúc: ${fmtDate(result.created_at)}</small> `; const emptyMsg = notesListUl.querySelector('.text-muted'); if (emptyMsg) emptyMsg.remove(); const errorMsg = notesListUl.querySelector('.text-danger'); if (errorMsg) errorMsg.remove(); notesListUl.insertBefore(li, notesListUl.firstChild); newNoteTextInput.value = ''; } catch(err) { const errorDiv = document.createElement('div'); errorDiv.className = 'alert alert-danger alert-sm mt-2'; errorDiv.textContent = `Lỗi thêm ghi chú: ${err.message}`; addNoteForm.appendChild(errorDiv); } finally { setButtonLoading(addNoteSubmitBtn, false); } }; }




        // Phân trang & Kiểu xem
        rowsPerPageEl.onchange = () => { pagination.rowsPerPage = +rowsPerPageEl.value; pagination.currentPage = 1; runDisplay(); };
        prevPageBtn.onclick = () => { if (pagination.currentPage > 1) { pagination.currentPage--; runDisplay(); } };
        nextPageBtn.onclick = () => { const totalPages = Math.ceil(currentRows.length / pagination.rowsPerPage) || 1; if (pagination.currentPage < totalPages) { pagination.currentPage++; runDisplay(); } };
        viewListBtn.onclick = () => { displayMode = 'list'; viewListBtn.classList.add('active'); viewGridBtn.classList.remove('active'); if (currentRows.length > 0) { listContainer.classList.remove('hidden'); gridContainer.classList.add('hidden'); } };
        viewGridBtn.onclick = () => { displayMode = 'grid'; viewListBtn.classList.remove('active'); viewGridBtn.classList.add('active'); if (currentRows.length > 0) { listContainer.classList.add('hidden'); gridContainer.classList.remove('hidden'); } };


    } // Kết thúc addEventListeners




    // --- Mở rộng tiện ích (Alert, Loading, State) ---
    function show(msg, type = 'success') { const a = $('status'); if (!a) return; a.textContent = msg; const alertType = (type === 'warn') ? 'alert-danger' : 'alert-success'; a.className = `alert ${alertType} shadow-sm`; a.classList.remove('hidden'); clearTimeout(a.timer); a.timer = setTimeout(() => { a.classList.add('hidden'); }, 3000); }
    function hide() { const a = $('status'); if (a) a.classList.add('hidden'); }
    function setButtonLoading(button, isLoading) { if (!button) return; const textEl = button.querySelector('.btn-text'); const spinnerEl = button.querySelector('.spinner-border'); if (isLoading) { button.disabled = true; button.classList.add('is-loading'); if (textEl) textEl.classList.add('hidden'); if (spinnerEl) spinnerEl.classList.remove('hidden'); } else { button.disabled = false; button.classList.remove('is-loading'); if (textEl) textEl.classList.remove('hidden'); if (spinnerEl) spinnerEl.classList.add('hidden'); } }
    function showResultState(innerHTML) { if (!resultStateContainer) return; resultStateContainer.innerHTML = innerHTML; resultStateContainer.classList.remove('hidden'); if (listContainer) listContainer.classList.add('hidden'); if (gridContainer) gridContainer.classList.add('hidden'); }
    function showLoadingState(message) { showResultState(` <div class="spinner-border text-primary" role="status" style="width: 3rem; height: 3rem;"> <span class="visually-hidden">Loading...</span> </div> <p class="h5 mt-3 state-message">${message}</p> `); }
    function showEmptyState(message, iconClass = 'bx bx-data') { showResultState(` <i class='state-icon ${iconClass}'></i> <p class="h5 mt-3 state-message">${message}</p> `); }
    function hideResultState() { if (resultStateContainer) { resultStateContainer.classList.add('hidden'); resultStateContainer.innerHTML = ''; } }




    // --- Khởi tạo ---
    checkSession(); // Bắt đầu bằng việc kiểm tra session


}); // Kết thúc DOMContentLoaded




