/* ========= 4. BIỂU TƯỢNG SẮP XẾP ========= */
.sort-icon::after { content: ' ⇅'; font-size: 0.8em; opacity: 0.4; }
th.sorted-asc .sort-icon::after { content: ' ↑'; opacity: 1; }
th.sorted-desc .sort-icon::after { content: ' ↓'; opacity: 1; }
th[data-sort] { cursor: pointer; }


/* ========= 5. BẢNG (TABLE) ========= */
.table-wrap { overflow-x: auto; border: none; border-radius: var(--card-radius); }
#resultTable { min-width: 1200px; margin-bottom: 0; font-size: 0.875rem; }
#resultTable th, #resultTable td { padding: 0.6rem 0.75rem; vertical-align: middle; border-bottom: 1px solid var(--bs-border-color-translucent); }
#resultTable thead th {
  position: sticky; top: 0; z-index: 10;
  background-color: var(--bs-body-bg); /* Nền dính */
  font-weight: 600;
  font-size: 0.8rem;
  text-transform: uppercase;
  color: var(--bs-secondary-color);
  border-bottom: 2px solid var(--bs-border-color);
}
/* Bỏ sọc vằn (striped) và thay bằng hover */
#resultTable {
