app.post('/api/get-bill', isAuthenticated, async (req, res) => {
    const { account, product_id } = req.body || {};
    if (!account || !product_id) return res.status(400).json({ error: 'Thiếu account hoặc product_id' });
    const payload = { account, product_id, custom_bill_amount: '', province: '', configurable: true };
    try {
        const upstream = await fetchWithTimeout( new URL(API_GET_BILL_PATH, API_BASE_URL).toString(), {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json, text/plain, */*',
                'a-csrftoken': API_CSRF_TOKEN,
                'a-from': 'APC-js', 'a-lang': 'vi', 'a-os': 'web',
                'a-timestamp': nowSec(),
                'Cookie': API_COOKIE,
                'Origin': API_BASE_URL,
                'Referer': `${API_BASE_URL}/`,
                'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/141.0.0.0 Safari/537.36'
            },
            body: JSON.stringify(payload)
        });
        const ct = upstream.headers.get('content-type') || '';
        const responseText = await upstream.text();
        if (!ct.includes('application/json')) { return res.status(upstream.status).json({ error: 'Lỗi từ API Cổng 1 (Có thể bị chặn)', details: responseText || 'Phản hồi không phải JSON' }); }
        let data;
        try { data = JSON.parse(responseText); }
        catch (parseErr) { console.error('Lỗi parse JSON Cổng 1:', parseErr.message, 'Response text:', responseText); return res.status(500).json({ error: 'API Cổng 1 trả về JSON không hợp lệ (có thể là rỗng)', details: parseErr.message }); }
        return res.status(upstream.status).json(data);
    } catch (err) { console.error('Error in /api/get-bill (fetch):', err); if (err.name === 'AbortError' || err.code === 'ETIMEDOUT') { return res.status(504).json({ error: 'Gateway Timeout (API Cổng 1 không phản hồi)' }); } return res.status(500).json({ error: err.message }); }
});


