            const curr = r.amount_current || r.curr || '0';
            const total = r.total || '0';
            const nhap = r.nhapat || r.nhap || '';
            const xuat = r.xuatat || r.xuat || '';
            const member = r.membername || r.memberName || '';
            const employee = r.employee_username || '';
            const cells = [
                { col: 'index', text: displayIndex, dataSort: displayIndex },
                { col: 'name', text: name },
                { col: 'address', text: address },
                { col: 'account', text: account },
                { col: 'prev', text: fmtMoney(prev), dataSort: prev },
                { col: 'curr', text: fmtMoney(curr), dataSort: curr },
                { col: 'total', text: fmtMoney(total), dataSort: total, dataTotal: total },
                { col: 'nhap', text: fmtDate(nhap), dataSort: nhap },
                { col: 'xuat', text: fmtDate(xuat), dataSort: xuat },
