        maxAge: 30 * 24 * 60 * 60 * 1000, // ** SỬA: Tăng lên 30 ngày cho "Ghi nhớ" **
        sameSite: 'none'
    },
}));


function isAuthenticated(req, res, next) { if (req.session.user) { next(); } else { res.status(401).json({ error: 'Chưa đăng nhập' }); } }
function isAdmin(req, res, next) { if (req.session.user && req.session.user.role === 'admin') { next(); } else { res.status(403).json({ error: 'Không có quyền truy cập' }); } }


