          CREATE TABLE IF NOT EXISTS members (
            id SERIAL PRIMARY KEY, name TEXT NOT NULL, zalo TEXT, bank TEXT
          );
        `);
        // Tạo bảng history
        await client.query(`
          CREATE TABLE IF NOT EXISTS history (
            id SERIAL PRIMARY KEY, account TEXT NOT NULL, provider_id TEXT NOT NULL,
            name TEXT, address TEXT, amount_current TEXT, amount_previous TEXT,
            total TEXT, nhapAt TIMESTAMPTZ, xuatAt TIMESTAMPTZ, soldAt TIMESTAMPTZ,
            memberId INTEGER REFERENCES members(id) ON DELETE SET NULL,
            memberName TEXT,
            employee_id INTEGER REFERENCES users(id) ON DELETE SET NULL,
            employee_username TEXT,
            raw JSONB
          );
        `);
         // Tạo bảng employees (bảng mới)
        await client.query(`
          CREATE TABLE IF NOT EXISTS employees (
