        // ** SỬA: GLogic tra cứu 1 nút duy nhất **
        lookupBtn.onclick = async () => {
            const providerValue = providerEl.value;
            const codes = accountsEl.value.split('\n').map(s => s.trim()).filter(s => s.length > 5);

            if (!codes.length) {
                return show('Vui lòng nhập mã khách hàng.', 'warn');
            }


            setButtonLoading(lookupBtn, true);
            let results = [];

            // Kiểm tra value của provider để chọn cổng (chỉ chứa số là Cổng 2)
            const isPort2 = /^\d{8}$/.test(providerValue);

            if (isPort2) {
                // --- LOGIC CỔNG 2 ---
                const sku = providerValue;
                showLoadingState(`(Cổng 2) Đang tra cứu ${codes.length} mã...`);

                for (let i = 0; i < codes.length; i++) {
                    let jsonResponse;
                    try {
                        const resp = await fetch('/api/check-electricity', { // ** SỬA: Gọi API mới **
                            method: 'POST',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify({
                                contract_number: codes[i], // ** SỬA: Dùng contract_number **
                                sku: sku
                            }),
                            credentials: 'include'
                        });

                        const contentType = resp.headers.get('content-type') || '';
                        const responseText = await resp.text();


                        if (!resp.ok || !contentType.includes('application/json')) {
                            if (resp.status === 401) { window.location.reload(); return; }
                            try { const errJson = JSON.parse(responseText); throw new Error(errJson.error || errJson.details || `Lỗi ${resp.status}`); }
                            catch(e) { throw new Error(responseText || `Lỗi ${resp.status} - Server không trả về JSON`); }
                        }

                        jsonResponse = JSON.parse(responseText);
                        const nr = normalizeResp_Port2(jsonResponse, codes[i]);
                        results.push(nr);

                    } catch (err) {
                        show(`Lỗi khi tra mã Cổng 2 ${codes[i]}: ${err.message}`, 'warn');
                        if (err.message.includes("Chưa đăng nhập")) break;
                        results.push(normalizeResp_Port2({ error: { message: err.message } }, codes[i]));
                    }

                    hideResultState(); renderResults(results);
                    if (i < codes.length - 1) {
                        showLoadingState(`(Cổng 2) Đang tra cứu mã tiếp theo... (${i + 1}/${codes.length})`);
                        await new Promise(r=>setTimeout(r,500));
                    }
                }

            } else {
