// ===================== KẾT NỐI DATABASE =====================
const { Pool } = pg;
const db = new Pool({
    connectionString: DATABASE_URL,
    ssl: { rejectUnauthorized: false }
});


async function initializeDatabase() {
    const client = await db.connect();
    console.log('Đã kết nối Database thành công.');
    try {
        // Tạo bảng users (bảng cũ, để tương thích)
         await client.query(`
          CREATE TABLE IF NOT EXISTS users (
            id SERIAL PRIMARY KEY,
            username TEXT UNIQUE NOT NULL,
            password_hash TEXT NOT NULL,
            role TEXT NOT NULL DEFAULT 'user' CHECK (role IN ('admin', 'user')),
            created_at TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP
          );
        `);
        // Tạo bảng kho
        await client.query(`
          CREATE TABLE IF NOT EXISTS kho (
            key TEXT PRIMARY KEY, account TEXT NOT NULL, provider_id TEXT NOT NULL,
            name TEXT, address TEXT, amount_current TEXT, amount_previous TEXT,
            total TEXT, nhapAt TIMESTAMPTZ, xuatAt TIMESTAMPTZ, raw JSONB
          );
        `);
        // Tạo bảng members
        await client.query(`
          CREATE TABLE IF NOT EXISTS members (
            id SERIAL PRIMARY KEY, name TEXT NOT NULL, zalo TEXT, bank TEXT
          );
        `);
        // Tạo bảng history
        await client.query(`
          CREATE TABLE IF NOT EXISTS history (
            id SERIAL PRIMARY KEY, account TEXT NOT NULL, provider_id TEXT NOT NULL,
            name TEXT, address TEXT, amount_current TEXT, amount_previous TEXT,
            total TEXT, nhapAt TIMESTAMPTZ, xuatAt TIMESTAMPTZ, soldAt TIMESTAMPTZ,
            memberId INTEGER REFERENCES members(id) ON DELETE SET NULL,
            memberName TEXT,
            employee_id INTEGER REFERENCES users(id) ON DELETE SET NULL,
            employee_username TEXT,
            raw JSONB
          );
        `);
         // Tạo bảng employees (bảng mới)
        await client.query(`
          CREATE TABLE IF NOT EXISTS employees (
            id SERIAL PRIMARY KEY,
            username TEXT UNIQUE NOT NULL,
            password_hash TEXT NOT NULL,
            role TEXT NOT NULL DEFAULT 'user' CHECK (role IN ('admin', 'user')),
            created_at TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP,
            full_name TEXT,
            phone TEXT,
            address TEXT,
            avatar_url TEXT,
            company_name TEXT,
            tax_code TEXT
          );
        `);
        // Tạo bảng work_notes
        await client.query(`
          CREATE TABLE IF NOT EXISTS work_notes (
            id SERIAL PRIMARY KEY,
            employee_id INTEGER REFERENCES employees(id) ON DELETE CASCADE,
            author_id INTEGER REFERENCES employees(id) ON DELETE SET NULL,
            author_username TEXT,
            note_text TEXT NOT NULL,
            created_at TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP
          );
        `);


        // Chuyển dữ liệu từ 'users' sang 'employees'
        const employeeCheck = await client.query('SELECT 1 FROM employees LIMIT 1');
        if (employeeCheck.rowCount === 0) {
            try {
                await client.query(`
                    INSERT INTO employees (id, username, password_hash, role, created_at)
                    SELECT id, username, password_hash, role, created_at FROM users
                    ON CONFLICT (id) DO NOTHING;
                `);
                await client.query("SELECT setval('employees_id_seq', (SELECT MAX(id) FROM employees));");
                console.log("Đã di chuyển dữ liệu từ 'users' sang 'employees'.");
            } catch (copyErr) {
                console.warn("Không thể di chuyển dữ liệu từ 'users' (có thể không tồn tại, bỏ qua):", copyErr.message);
            }
        }


        // Tạo admin mặc định trong bảng 'employees'
        const adminCheckEmployees = await client.query('SELECT 1 FROM employees WHERE username = $1', ['admin']);
        if (adminCheckEmployees.rowCount === 0) {
            const adminPassword = '123456';
            const saltRounds = 10;
            const hashedPassword = await bcrypt.hash(adminPassword, saltRounds);
            await client.query(
                'INSERT INTO employees (username, password_hash, role, full_name) VALUES ($1, $2, $3, $4)',
                ['admin', hashedPassword, 'admin', 'Quản Trị Viên']
            );
            console.log(`Đã tạo tài khoản admin mặc định: admin / ${adminPassword}`);
        }


        console.log('Đã khởi tạo các bảng (tables) thành công.');
    } catch (err) { console.error('Lỗi khi khởi tạo database:', err); throw err; }
    finally { client.release(); }
}




