app.get('/api/employees', isAuthenticated, isAdmin, async (req, res) => {
    const { search } = req.query;
    try {
        let query = 'SELECT id, username, role, full_name, phone, address, avatar_url, company_name, tax_code FROM employees ORDER BY username';
        let params = [];
        if (search) {
            query = 'SELECT id, username, role, full_name, phone, address, avatar_url, company_name, tax_code FROM employees WHERE username ILIKE $1 OR full_name ILIKE $1 ORDER BY username';
            params = [`%${search}%`];
        }
