// ===================== KẾT NỐI DATABASE =====================
const { Pool } = pg;
const db = new Pool({
    connectionString: DATABASE_URL,
    ssl: { rejectUnauthorized: false }
});


async function initializeDatabase() {
    const client = await db.connect();
    console.log('Đã kết nối Database thành công.');
    try {
        // Tạo bảng users (bảng cũ, để tương thích)
         await client.query(`
          CREATE TABLE IF NOT EXISTS users (
            id SERIAL PRIMARY KEY,
            username TEXT UNIQUE NOT NULL,
            password_hash TEXT NOT NULL,
            role TEXT NOT NULL DEFAULT 'user' CHECK (role IN ('admin', 'user')),
            created_at TIMESTAMPTZ DEFAULT CURRENT_TIMESTAMP
          );
        `);
        // Tạo bảng kho
        await client.query(`
          CREATE TABLE IF NOT EXISTS kho (
            key TEXT PRIMARY KEY, account TEXT NOT NULL, provider_id TEXT NOT NULL,
            name TEXT, address TEXT, amount_current TEXT, amount_previous TEXT,
            total TEXT, nhapAt TIMESTAMPTZ, xuatAt TIMESTAMPTZ, raw JSONB
          );
        `);
        // Tạo bảng members
        await client.query(`
          CREATE TABLE IF NOT EXISTS members (
            id SERIAL PRIMARY KEY, name TEXT NOT NULL, zalo TEXT, bank TEXT
          );
        `);
        // Tạo bảng history
        await client.query(`
          CREATE TABLE IF NOT EXISTS history (
