app.post('/api/employees', isAuthenticated, isAdmin, async (req, res) => {
    const { username, password, role, full_name, phone, address, avatar_url, company_name, tax_code } = req.body;
    if (!username || !password || password.length < 6 || !role) { return res.status(400).json({ error: 'Tên đăng nhập, mật khẩu (>= 6 ký tự) và quyền là bắt buộc.' }); }
    try {
        const saltRounds = 10;
        const hashedPassword = await bcrypt.hash(password, saltRounds);
        const result = await db.query( `INSERT INTO employees (username, password_hash, role, full_name, phone, address, avatar_url, company_name, tax_code) VALUES ($1, $2, $3, $4, $5, $6, $7, $8, $9) RETURNING id, username, role, full_name`, [username, hashedPassword, role, full_name, phone, address, avatar_url, company_name, tax_code] );
        res.status(201).json(result.rows[0]);
    } catch (err) {
        if (err.code === '23505') { return res.status(409).json({ error: 'Tên đăng nhập đã tồn tại.' }); }
        console.error('Lỗi /api/employees (POST):', err); res.status(500).json({ error: err.message });
    }
});
app.put('/api/employees/:id', isAuthenticated, isAdmin, async (req, res) => {
    const { id } = req.params;
    const { username, password, role, full_name, phone, address, avatar_url, company_name, tax_code } = req.body;
    if (!username || !role) { return res.status(400).json({ error: 'Tên đăng nhập và quyền là bắt buộc.' }); }
    if (password && password.length < 6) { return res.status(400).json({ error: 'Mật khẩu mới (nếu nhập) phải có ít nhất 6 ký tự.' }); }
    try {
        let hashedPassword;
        if (password) { const saltRounds = 10; hashedPassword = await bcrypt.hash(password, saltRounds); }
        const query = password
            ? 'UPDATE employees SET username = $1, password_hash = $2, role = $3, full_name = $4, phone = $5, address = $6, avatar_url = $7, company_name = $8, tax_code = $9 WHERE id = $10 RETURNING id, username, role, full_name'
            : 'UPDATE employees SET username = $1, role = $3, full_name = $4, phone = $5, address = $6, avatar_url = $7, company_name = $8, tax_code = $9 WHERE id = $10 RETURNING id, username, role, full_name';
        const params = password
            ? [username, hashedPassword, role, full_name, phone, address, avatar_url, company_name, tax_code, id]
            : [username, role, full_name, phone, address, avatar_url, company_name, tax_code, id];
        const result = await db.query(query, params);
        if (result.rowCount === 0) { return res.status(404).json({ error: 'Không tìm thấy nhân viên.' }); }
        res.json(result.rows[0]);
    } catch (err) {
        if (err.code === '23505') { return res.status(409).json({ error: 'Tên đăng nhập đã tồn tại.' }); }
        console.error('Lỗi /api/employees (PUT):', err); res.status(500).json({ error: err.message });
    }
});
