        if (editEmployeeBtn) { editEmployeeBtn.onclick = () => { const selectedOption = employeeSelect.options[employeeSelect.selectedIndex]; if (!selectedOption) return show('Vui lòng chọn nhân viên cần sửa.', 'warn'); const employeeData = JSON.parse(selectedOption.dataset.employeeData); employeeForm.reset(); employeeError.classList.add('hidden'); employeeIdInput.value = employeeData.id; employeeUsernameInput.value = employeeData.username; employeePasswordInput.removeAttribute('required'); employeePasswordInput.placeholder = "Để trống nếu không muốn đổi mật khẩu"; employeeRoleSelect.value = employeeData.role; employeeFullNameInput.value = employeeData.full_name || ''; employeePhoneInput.value = employeeData.phone || ''; employeeAddressInput.value = employeeData.address || ''; employeeAvatarUrlInput.value = employeeData.avatar_url || ''; employeeCompanyNameInput.value = employeeData.company_name || ''; employeeTaxCodeInput.value = employeeData.tax_code || ''; employeeModalEl.querySelector('.modal-title').textContent = 'Sửa Thông Tin Nhân Viên'; employeeModal.show(); }; }
        if (deleteEmployeeBtn) { deleteEmployeeBtn.onclick = async () => { const selectedOption = employeeSelect.options[employeeSelect.selectedIndex]; if (!selectedOption) return show('Vui lòng chọn nhân viên cần xóa.', 'warn'); const employeeId = selectedOption.value; const employeeName = selectedOption.text.split(' (')[0]; if (!confirm(`Bạn có chắc chắn muốn xóa nhân viên "${employeeName}" (ID: ${employeeId})? Hành động này không thể hoàn tác.`)) return; setButtonLoading(deleteEmployeeBtn, true); try { const resp = await fetch(`/api/employees/${employeeId}`, { method: 'DELETE', credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); show(`Đã xóa nhân viên ${employeeName}`, 'success'); await refreshEmployees(); } catch (err) { show(`Lỗi xóa nhân viên: ${err.message}`, 'warn'); } finally { setButtonLoading(deleteEmployeeBtn, false); } }; }
        if (employeeForm) { employeeForm.onsubmit = async (e) => { e.preventDefault(); const id = employeeIdInput.value; const isEditing = !!id; const url = isEditing ? `/api/employees/${id}` : '/api/employees'; const method = isEditing ? 'PUT' : 'POST'; const password = employeePasswordInput.value; if (!isEditing && (!password || password.length < 6)) { employeeError.textContent = 'Mật khẩu phải có ít nhất 6 ký tự.'; employeeError.classList.remove('hidden'); return; } if (isEditing && password && password.length < 6) { employeeError.textContent = 'Mật khẩu mới (nếu nhập) phải có ít nhất 6 ký tự.'; employeeError.classList.remove('hidden'); return; } const employeeData = { username: employeeUsernameInput.value.trim(), password: password || undefined, role: employeeRoleSelect.value, full_name: employeeFullNameInput.value.trim() || null, phone: employeePhoneInput.value.trim() || null, address: employeeAddressInput.value.trim() || null, avatar_url: employeeAvatarUrlInput.value.trim() || null, company_name: employeeCompanyNameInput.value.trim() || null, tax_code: employeeTaxCodeInput.value.trim() || null, }; setButtonLoading(employeeSubmitBtn, true); employeeError.classList.add('hidden'); try { const resp = await fetch(url, { method: method, headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(employeeData), credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); show(isEditing ? 'Đã cập nhật nhân viên' : 'Đã thêm nhân viên mới', 'success'); employeeModal.hide(); await refreshEmployees(); } catch (err) { employeeError.textContent = err.message; employeeError.classList.remove('hidden'); } finally { setButtonLoading(employeeSubmitBtn, false); } }; }

        // --- Notes Modal Events ---
        if (viewNotesBtn) { viewNotesBtn.onclick = async () => { const selectedOption = employeeSelect.options[employeeSelect.selectedIndex]; if (!selectedOption) return show('Vui lòng chọn nhân viên để xem ghi chú.', 'warn'); const employeeId = selectedOption.value; const employeeName = selectedOption.text.split(' (')[0]; notesEmployeeNameSpan.textContent = employeeName; noteEmployeeIdInput.value = employeeId; notesListUl.innerHTML = '<li class="list-group-item text-muted">Đang tải ghi chú...</li>'; newNoteTextInput.value = ''; addNoteForm.querySelector('.alert')?.remove(); notesModal.show(); try { const resp = await fetch(`/api/employees/${employeeId}/notes`, { credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); notesListUl.innerHTML = ''; if (result.length === 0) { notesListUl.innerHTML = '<li class="list-group-item text-muted">Chưa có ghi chú nào.</li>'; } else { result.forEach(note => { const li = document.createElement('li'); li.className = 'list-group-item'; li.innerHTML = ` <p class="mb-1">${note.note_text}</p> <small class="note-meta">Bởi: ${note.author_username || 'N/A'} - Lúc: ${fmtDate(note.created_at)}</small> `; notesListUl.appendChild(li); }); } } catch (err) { notesListUl.innerHTML = `<li class="list-group-item text-danger">Lỗi tải ghi chú: ${err.message}</li>`; } }; }
        if (addNoteForm) { addNoteForm.onsubmit = async (e) => { e.preventDefault(); const employeeId = noteEmployeeIdInput.value; const noteText = newNoteTextInput.value.trim(); if (!employeeId || !noteText) return; addNoteForm.querySelector('.alert')?.remove(); setButtonLoading(addNoteSubmitBtn, true); try { const resp = await fetch(`/api/employees/${employeeId}/notes`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ note_text: noteText }), credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); const li = document.createElement('li'); li.className = 'list-group-item'; li.innerHTML = ` <p class="mb-1">${result.note_text}</p> <small class="note-meta">Bởi: ${result.author_username || currentUser.username} - Lúc: ${fmtDate(result.created_at)}</small> `; const emptyMsg = notesListUl.querySelector('.text-muted'); if (emptyMsg) emptyMsg.remove(); const errorMsg = notesListUl.querySelector('.text-danger'); if (errorMsg) errorMsg.remove(); notesListUl.insertBefore(li, notesListUl.firstChild); newNoteTextInput.value = ''; } catch(err) { const errorDiv = document.createElement('div'); errorDiv.className = 'alert alert-danger alert-sm mt-2'; errorDiv.textContent = `Lỗi thêm ghi chú: ${err.message}`; addNoteForm.appendChild(errorDiv); } finally { setButtonLoading(addNoteSubmitBtn, false); } }; }




        // Phân trang & Kiểu xem
        rowsPerPageEl.onchange = () => { pagination.rowsPerPage = +rowsPerPageEl.value; pagination.currentPage = 1; runDisplay(); };
        prevPageBtn.onclick = () => { if (pagination.currentPage > 1) { pagination.currentPage--; runDisplay(); } };
        nextPageBtn.onclick = () => { const totalPages = Math.ceil(currentRows.length / pagination.rowsPerPage) || 1; if (pagination.currentPage < totalPages) { pagination.currentPage++; runDisplay(); } };
        viewListBtn.onclick = () => { displayMode = 'list'; viewListBtn.classList.add('active'); viewGridBtn.classList.remove('active'); if (currentRows.length > 0) { listContainer.classList.remove('hidden'); gridContainer.classList.add('hidden'); } };
        viewGridBtn.onclick = () => { displayMode = 'grid'; viewListBtn.classList.remove('active'); viewGridBtn.classList.add('active'); if (currentRows.length > 0) { listContainer.classList.add('hidden'); gridContainer.classList.remove('hidden'); } };


    } // Kết thúc addEventListeners




    // --- Mở rộng tiện ích (Alert, Loading, State) ---
    function show(msg, type = 'success') { const a = $('status'); if (!a) return; a.textContent = msg; const alertType = (type === 'warn') ? 'alert-danger' : 'alert-success'; a.className = `alert ${alertType} shadow-sm`; a.classList.remove('hidden'); clearTimeout(a.timer); a.timer = setTimeout(() => { a.classList.add('hidden'); }, 3000); }
    function hide() { const a = $('status'); if (a) a.classList.add('hidden'); }
    function setButtonLoading(button, isLoading) { if (!button) return; const textEl = button.querySelector('.btn-text'); const spinnerEl = button.querySelector('.spinner-border'); if (isLoading) { button.disabled = true; button.classList.add('is-loading'); if (textEl) textEl.classList.add('hidden'); if (spinnerEl) spinnerEl.classList.remove('hidden'); } else { button.disabled = false; button.classList.remove('is-loading'); if (textEl) textEl.classList.remove('hidden'); if (spinnerEl) spinnerEl.classList.add('hidden'); } }
    function showResultState(innerHTML) { if (!resultStateContainer) return; resultStateContainer.innerHTML = innerHTML; resultStateContainer.classList.remove('hidden'); if (listContainer) listContainer.classList.add('hidden'); if (gridContainer) gridContainer.classList.add('hidden'); }
    function showLoadingState(message) { showResultState(` <div class="spinner-border text-primary" role="status" style="width: 3rem; height: 3rem;"> <span class="visually-hidden">Loading...</span> </div> <p class="h5 mt-3 state-message">${message}</p> `); }
    function showEmptyState(message, iconClass = 'bx bx-data') { showResultState(` <i class='state-icon ${iconClass}'></i> <p class="h5 mt-3 state-message">${message}</p> `); }
    function hideResultState() { if (resultStateContainer) { resultStateContainer.classList.add('hidden'); resultStateContainer.innerHTML = ''; } }




    // --- Khởi tạo ---
    checkSession(); // Bắt đầu bằng việc kiểm tra session


}); // Kết thúc DOMContentLoaded




