app.post('/api/login', async (req, res) => {
    // ** SỬA: Thêm rememberMe **
    const { username, password, rememberMe } = req.body;
    if (!username || !password) { return res.status(400).json({ error: 'Thiếu tên đăng nhập hoặc mật khẩu' }); }
    try {
        const result = await db.query('SELECT id, username, password_hash, role FROM employees WHERE username = $1', [username]);
        if (result.rowCount === 0) { return res.status(401).json({ error: 'Tên đăng nhập hoặc mật khẩu không đúng' }); }
        const user = result.rows[0];
        const match = await bcrypt.compare(password, user.password_hash);
        if (match) {
            req.session.user = { id: user.id, username: user.username, role: user.role };
            // ** SỬA: Cập nhật maxAge nếu "Ghi nhớ" = true **
            if (rememberMe) {
                req.session.cookie.maxAge = 30 * 24 * 60 * 60 * 1000; // 30 ngày
            } else {
                req.session.cookie.maxAge = 24 * 60 * 60 * 1000; // 1 ngày (mặc định)
            }
            res.json({ id: user.id, username: user.username, role: user.role });
        } else {
            res.status(401).json({ error: 'Tên đăng nhập hoặc mật khẩu không đúng' });
        }
    } catch (err) { console.error('Lỗi /api/login:', err); res.status(500).json({ error: 'Lỗi máy chủ nội bộ' }); }
});
app.post('/api/logout', (req, res) => { req.session.destroy(err => { if (err) { console.error('Lỗi khi đăng xuất:', err); return res.status(500).json({ error: 'Lỗi khi đăng xuất' }); } res.clearCookie('connect.sid'); res.json({ message: 'Đăng xuất thành công' }); }); });
