        const hideZero = hideZeroBtn.checked;
        currentRows = allRows.filter(r => {
            if (hideZero && parseMoney(r.total) === 0) return false;
            if (term) {
                const content = [r.name, r.address, r.account, r.employee_username].join(' ').toLowerCase();
                if (!content.includes(term)) return false;
            }
            return true;
        });
        currentRows.sort((a, b) => {
            let va, vb;
            if (sortKey === 'index') { va = allRows.indexOf(a); vb = allRows.indexOf(b); }
            else if (sortKey === 'nhap') { va = a.nhapat || a.nhap; vb = b.nhapat || b.nhap; }
            else if (sortKey === 'xuat') { va = a.xuatat || a.xuat; vb = b.xuatat || b.xuat; }
            else if (sortKey === 'member') { va = a.membername || a.memberName; vb = b.membername || b.memberName; }
            else if (sortKey === 'employee') { va = a.employee_username; vb = b.employee_username; }
            else if (sortKey === 'prev') { va = a.amount_previous || r.prev; vb = b.amount_previous || b.prev; }
            else if (sortKey === 'curr') { va = a.amount_current || r.curr; vb = b.amount_current || b.curr; }
            else { va = a[sortKey]; vb = b[sortKey]; }
            let cmp = 0;
            const type = thead?.querySelector(`th[data-col="${sortKey}"]`)?.dataset.sort || 'text';
            if (type === 'money') cmp = (Number(va) || 0) - (Number(vb) || 0);
            else if (type === 'index') cmp = va - vb;
            else if (type === 'date') cmp = (va || '').localeCompare(vb || '');
            else cmp = (va || '').toString().localeCompare((vb || '').toString(), 'vi', { sensitivity: 'base' });
            return sortDir * cmp;
        });
