        khoImportBtn.onclick = async () => { const checkedBoxes = Array.from(tbody.querySelectorAll('input[data-key]:checked')); const keysToImport = checkedBoxes.map(cb => cb.dataset.key); if (!keysToImport.length) return show('Chọn trước bill', 'warn'); const billsToImport = keysToImport.map(k => allRows.find(r => (r.key || r.id) == k)).filter(Boolean).map(b => ({ ...b, nhap: new Date().toISOString() })); setButtonLoading(khoImportBtn, true); try { const resp = await fetch('/api/kho/import', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ bills: billsToImport }), credentials: 'include' }); if (!resp.ok) throw new Error( (await resp.json()).error || `Lỗi ${resp.status}`); const data = await resp.json(); show(`Đã nhập ${data.added} bill mới. Tổng KHO: ${data.total}`, 'success'); } catch (err) { show(`Lỗi nhập KHO: ${err.message}`, 'warn'); } finally { setButtonLoading(khoImportBtn, false); } };
        khoListBtn.onclick = async () => { viewMode = 'kho'; setButtonLoading(khoListBtn, true); showLoadingState('Đang tải dữ liệu KHO...'); try { const resp = await fetch('/api/kho/list', { credentials: 'include' }); if (!resp.ok) throw new Error( (await resp.json()).error || `Lỗi ${resp.status}`); const khoRows = await resp.json(); renderResults(khoRows); if (khoRows.length > 0) show(`KHO: ${khoRows.length} bill`, 'success'); } catch (err) { show(`Lỗi tải KHO: ${err.message}`, 'warn'); showEmptyState(`Lỗi tải KHO: ${err.message}`, 'bx bx-error-circle'); } finally { setButtonLoading(khoListBtn, false); } };
        khoRemoveBtn.onclick = async () => { const keys = Array.from(tbody.querySelectorAll('input[data-key]:checked')).map(cb => cb.dataset.key); if (!keys.length) return show('Chọn trước bill xoá', 'warn'); if (!confirm(`Bạn chắc chắn muốn xóa ${keys.length} bill này khỏi KHO?`)) return; setButtonLoading(khoRemoveBtn, true); try { const resp = await fetch('/api/kho/remove', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ keys }), credentials: 'include' }); if (!resp.ok) throw new Error( (await resp.json()).error || `Lỗi ${resp.status}`); const data = await resp.json(); show(`Đã xoá ${data.removed} bill khỏi KHO`, 'success'); if (viewMode === 'kho') khoListBtn.click(); } catch (err) { show(`Lỗi xóa KHO: ${err.message}`, 'warn'); } finally { setButtonLoading(khoRemoveBtn, false); } };
        pickBtn.onclick = () => { if (viewMode !== 'kho') { show('Chỉ lọc được khi đang xem KHO', 'warn'); khoListBtn.click(); return; } const from = +targetFrom.value * 100000 || 0; const to = +targetTo.value * 100000 || Infinity; if (from === 0 && to === Infinity) { return show('Vui lòng nhập số tiền Từ hoặc Đến', 'warn'); } const allKho = allRows; const filtered = allKho.filter(r => { const total = parseMoney(r.total); return total >= from && total <= to; }); renderResults(filtered); show(`Đã lọc KHO: ${filtered.length} bill (Từ ${fmtMoney(from)} đến ${fmtMoney(to)})`, 'success'); };
        sellBtn.onclick = async () => { const mid = memberSelect.value; if (!mid) return show('Chọn Khách Hàng Thẻ', 'warn'); const m = memberCache.find(x => String(x.id) === mid); if (!m) return show('KHT không hợp lệ', 'warn'); const keys = Array.from(tbody.querySelectorAll('input[data-key]:checked')).map(cb => cb.dataset.key); if (!keys.length) return show('Chọn bill để bán', 'warn'); setButtonLoading(sellBtn, true); try { const resp = await fetch('/api/sell', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ memberId: mid, memberName: m.name, keys: keys, soldAt: new Date().toISOString() }), credentials: 'include' }); if (!resp.ok) throw new Error( (await resp.json()).error || `Lỗi ${resp.status}`); const data = await resp.json(); show(`Đã bán ${data.sold_count} bill cho ${m.name}`, 'success'); if (viewMode === 'kho') khoListBtn.click(); } catch (err) { show(`Lỗi khi bán: ${err.message}`, 'warn'); } finally { setButtonLoading(sellBtn, false); } };
