        if (addEmployeeBtn) { addEmployeeBtn.onclick = () => { employeeForm.reset(); employeeIdInput.value = ''; employeeError.classList.add('hidden'); employeePasswordInput.setAttribute('required', 'required'); employeePasswordInput.placeholder = "Ít nhất 6 ký tự"; employeeModalEl.querySelector('.modal-title').textContent = 'Thêm Nhân Viên Mới'; employeeModal.show(); }; }
        if (editEmployeeBtn) { editEmployeeBtn.onclick = () => { const selectedOption = employeeSelect.options[employeeSelect.selectedIndex]; if (!selectedOption) return show('Vui lòng chọn nhân viên cần sửa.', 'warn'); const employeeData = JSON.parse(selectedOption.dataset.employeeData); employeeForm.reset(); employeeError.classList.add('hidden'); employeeIdInput.value = employeeData.id; employeeUsernameInput.value = employeeData.username; employeePasswordInput.removeAttribute('required'); employeePasswordInput.placeholder = "Để trống nếu không muốn đổi mật khẩu"; employeeRoleSelect.value = employeeData.role; employeeFullNameInput.value = employeeData.full_name || ''; employeePhoneInput.value = employeeData.phone || ''; employeeAddressInput.value = employeeData.address || ''; employeeAvatarUrlInput.value = employeeData.avatar_url || ''; employeeCompanyNameInput.value = employeeData.company_name || ''; employeeTaxCodeInput.value = employeeData.tax_code || ''; employeeModalEl.querySelector('.modal-title').textContent = 'Sửa Thông Tin Nhân Viên'; employeeModal.show(); }; }
        if (deleteEmployeeBtn) { deleteEmployeeBtn.onclick = async () => { const selectedOption = employeeSelect.options[employeeSelect.selectedIndex]; if (!selectedOption) return show('Vui lòng chọn nhân viên cần xóa.', 'warn'); const employeeId = selectedOption.value; const employeeName = selectedOption.text.split(' (')[0]; if (!confirm(`Bạn có chắc chắn muốn xóa nhân viên "${employeeName}" (ID: ${employeeId})? Hành động này không thể hoàn tác.`)) return; setButtonLoading(deleteEmployeeBtn, true); try { const resp = await fetch(`/api/employees/${employeeId}`, { method: 'DELETE', credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); show(`Đã xóa nhân viên ${employeeName}`, 'success'); await refreshEmployees(); } catch (err) { show(`Lỗi xóa nhân viên: ${err.message}`, 'warn'); } finally { setButtonLoading(deleteEmployeeBtn, false); } }; }
        if (employeeForm) { employeeForm.onsubmit = async (e) => { e.preventDefault(); const id = employeeIdInput.value; const isEditing = !!id; const url = isEditing ? `/api/employees/${id}` : '/api/employees'; const method = isEditing ? 'PUT' : 'POST'; const password = employeePasswordInput.value; if (!isEditing && (!password || password.length < 6)) { employeeError.textContent = 'Mật khẩu phải có ít nhất 6 ký tự.'; employeeError.classList.remove('hidden'); return; } if (isEditing && password && password.length < 6) { employeeError.textContent = 'Mật khẩu mới (nếu nhập) phải có ít nhất 6 ký tự.'; employeeError.classList.remove('hidden'); return; } const employeeData = { username: employeeUsernameInput.value.trim(), password: password || undefined, role: employeeRoleSelect.value, full_name: employeeFullNameInput.value.trim() || null, phone: employeePhoneInput.value.trim() || null, address: employeeAddressInput.value.trim() || null, avatar_url: employeeAvatarUrlInput.value.trim() || null, company_name: employeeCompanyNameInput.value.trim() || null, tax_code: employeeTaxCodeInput.value.trim() || null, }; setButtonLoading(employeeSubmitBtn, true); employeeError.classList.add('hidden'); try { const resp = await fetch(url, { method: method, headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(employeeData), credentials: 'include' }); const result = await resp.json(); if (!resp.ok) throw new Error(result.error || `Lỗi ${resp.status}`); show(isEditing ? 'Đã cập nhật nhân viên' : 'Đã thêm nhân viên mới', 'success'); employeeModal.hide(); await refreshEmployees(); } catch (err) { employeeError.textContent = err.message; employeeError.classList.remove('hidden'); } finally { setButtonLoading(employeeSubmitBtn, false); } }; }

