app.post('/api/check-electricity', isAuthenticated, async (req, res) => {
    // ** SỬA: Đổi tên API để khớp với 7ty.vn **
    const { contract_number, sku } = req.body || {};
    if (!contract_number || !sku) {
        return res.status(400).json({ error: 'Thiếu contract_number hoặc sku' });
    }

    const payload = {
        contract_number: contract_number,
        sku: sku
    };

    try {
        const upstream = await fetchWithTimeout(
            new URL(NEW_API_PATH, NEW_API_BASE_URL).toString(),
            {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    // ** SỬA: Xóa bỏ các header giả mạo (API 7ty.vn không cần) **
                },
                body: JSON.stringify(payload)
            }
        );


        const ct = upstream.headers.get('content-type') || '';
        const responseText = await upstream.text();


        if (!ct.includes('application/json')) {
            return res.status(upstream.status).json({
                error: 'Lỗi từ API Cổng 2 (7ty.vn)',
                details: responseText || 'Phản hồi không phải JSON'
            });
        }


        let data;
        try { data = JSON.parse(responseText); }
        catch (parseErr) {
            console.error('Lỗi parse JSON Cổng 2:', parseErr.message, 'Response text:', responseText);
            return res.status(500).json({
                error: 'API Cổng 2 trả về JSON không hợp lệ (có thể là rỗng)',
                details: parseErr.message
            });
        }
        return res.status(upstream.status).json(data);
    } catch (err) {
        console.error('Error in /api/check-electricity (fetch):', err);
        if (err.name === 'AbortError' || err.code === 'ETIMEDOUT') {
            return res.status(504).json({ error: 'Gateway Timeout (API Cổng 2 không phản hồi)' });
        }
        return res.status(500).json({ error: err.message });
    }
});




