    // --- Initialization ---
    function initializeAppElements() {
        providerEl = $('provider'); accountsEl = $('accounts'); filterDupBtn = $('filterDupBtn');

        // ** SỬA: Lấy sortAccountsBtn (nếu tồn tại) **
        sortAccountsBtn = $('sortAccountsBtn');

        lookupBtn = $('lookupBulkBtn');


        searchInput = $('searchInput'); hideZeroBtn = $('hideZeroToggle'); exportBtn = $('exportCsvBtn');
        copyBtn = $('copyBtn'); selectAllCb = $('selectAllCb'); khoImportBtn = $('khoImportBtn');
        khoListBtn = $('khoListBtn'); khoRemoveBtn = $('khoRemoveBtn'); memberAddBtn = $('memberAddBtn');
        memberEditBtn = $('memberEditBtn'); memberSearch = $('memberSearch'); memberSelect = $('memberSelect');
        targetFrom = $('targetFrom'); targetTo = $('targetTo'); pickBtn = $('pickBtn');
        sellBtn = $('sellBtn'); historyBtn = $('historyBtn'); table = $('resultTable');
        if (table) { thead = table.querySelector('thead'); tbody = table.querySelector('tbody'); tfoot = table.querySelector('tfoot'); sumTotalEl = table.querySelector('#sumTotal'); }
        else { thead = tbody = tfoot = sumTotalEl = null; }
        colToggles = document.querySelectorAll('.col-options input[data-col]');
        resultStateContainer = $('resultState'); listContainer = $('listContainer'); gridContainer = $('gridContainer');
        viewListBtn = $('viewListBtn'); viewGridBtn = $('viewGridBtn'); rowsPerPageEl = $('rowsPerPage');
        pageInfoEl = $('pageInfo'); prevPageBtn = $('prevPageBtn'); nextPageBtn = $('nextPageBtn');
        memberViewBtn = $('memberViewBtn');
        employeeSearchInput = $('employeeSearchInput'); employeeSearchBtn = $('employeeSearchBtn');
        employeeSelect = $('employeeSelect'); addEmployeeBtn = $('addEmployeeBtn');
        editEmployeeBtn = $('editEmployeeBtn'); deleteEmployeeBtn = $('deleteEmployeeBtn');
        viewNotesBtn = $('viewNotesBtn');
        employeeModalEl = $('employeeModal'); employeeModal = new bootstrap.Modal(employeeModalEl);
        employeeForm = $('employeeForm'); employeeIdInput = $('employeeId');
        employeeUsernameInput = $('employeeUsername'); employeePasswordInput = $('employeePassword');
        employeeRoleSelect = $('employeeRole'); employeeFullNameInput = $('employeeFullName');
        employeePhoneInput = $('employeePhone'); employeeAddressInput = $('employeeAddress');
        employeeAvatarUrlInput = $('employeeAvatarUrl'); employeeCompanyNameInput = $('employeeCompanyName');
        employeeTaxCodeInput = $('employeeTaxCode'); employeeError = $('employeeError');
        employeeSubmitBtn = employeeForm.querySelector('button[type="submit"]');
        notesModalEl = $('notesModal'); notesModal = new bootstrap.Modal(notesModalEl);
        notesEmployeeNameSpan = $('notesEmployeeName'); notesListUl = $('notesList');
        addNoteForm = $('addNoteForm'); noteEmployeeIdInput = $('noteEmployeeId');
        newNoteTextInput = $('newNoteText'); addNoteSubmitBtn = addNoteForm.querySelector('button[type="submit"]');


        addEventListeners(); // Gán sự kiện sau khi lấy elements
    }




    // --- Authentication ---
    loginForm.onsubmit = async (e) => {
        e.preventDefault();
        const username = usernameInput.value.trim();
        const password = passwordInput.value;
        const rememberMe = rememberMeCheckbox.checked; // Lấy giá trị checkbox
        if (!username || !password) return;
        setButtonLoading(loginSubmitBtn, true);
        loginError.classList.add('hidden');
        try {
            const response = await fetch('/api/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password, rememberMe }), // Gửi rememberMe
                credentials: 'include'
            });
            const result = await response.json();
            if (!response.ok) throw new Error(result.error || `Lỗi ${response.status}`);
            currentUser = result;
            initializeAppUI();
            loginModal.hide();
        } catch (err) {
            loginError.textContent = err.message;
            loginError.classList.remove('hidden');
        } finally {
            setButtonLoading(loginSubmitBtn, false);
        }
    };
    logoutBtn.onclick = async () => {
        setButtonLoading(logoutBtn, true);
        try {
            await fetch('/api/logout', { method: 'POST', credentials: 'include' });
            currentUser = null;
            appHeader.classList.add('hidden');
            appContent.classList.add('hidden');
            document.body.classList.remove('is-admin', 'is-user');
            usernameInput.value = '';
            passwordInput.value = '';
            loginError.classList.add('hidden');
            allRows = [];
            currentRows = [];
            if (tbody) tbody.innerHTML = '';
            hideResultState();
            loginModal.show();
        } catch (err) {
            show(`Lỗi khi đăng xuất: ${err.message}`, 'warn');
        } finally {
            setButtonLoading(logoutBtn, false);
        }
    };
    async function checkSession() {
        try {
            const response = await fetch('/api/session', { credentials: 'include' });
            const result = await response.json();
            if (response.ok && result.username) {
                currentUser = result;
                initializeAppUI();
            } else {
                loginModal.show();
            }
        } catch (err) {
            console.error("Lỗi kiểm tra session:", err);
            loginModal.show();
        }
    }
    function initializeAppUI() {
        initializeAppElements();
        appHeader.classList.remove('hidden');
        appContent.classList.remove('hidden');
        usernameDisplay.textContent = `Chào, ${currentUser.username} (${currentUser.role})!`;
        document.body.classList.remove('is-admin', 'is-user');
        document.body.classList.add(currentUser.role === 'admin' ? 'is-admin' : 'is-user');
        if (table) {
             applyToggles();
        }
        refreshMembers();
        refreshEmployees();
        showEmptyState('Bắt đầu bằng cách tra cứu hoặc mở KHO', 'bx bx-search');
    }


    // --- Table & Grid Rendering ---
    function renderResults(rows) {
        allRows = rows;
        pagination.currentPage = 1;
        applyFiltersAndSort();
        if (currentRows.length === 0) {
            showEmptyState('Không tìm thấy dữ liệu', 'bx bx-data');
        } else {
            hideResultState();
            runDisplay();
        }
    }
    function runDisplay() {
        if (!tbody || !pageInfoEl || !prevPageBtn || !nextPageBtn || !listContainer || !gridContainer) return;
        const totalPages = Math.ceil(currentRows.length / pagination.rowsPerPage) || 1;
        if (pagination.currentPage > totalPages) pagination.currentPage = totalPages;
        const start = (pagination.currentPage - 1) * pagination.rowsPerPage;
        const end = start + pagination.rowsPerPage;
        const paginatedRows = currentRows.slice(start, end);
        if (displayMode === 'list') {
            renderList(paginatedRows);
            listContainer.classList.remove('hidden');
            gridContainer.classList.add('hidden');
        } else {
            renderGrid(paginatedRows);
            listContainer.classList.add('hidden');
            gridContainer.classList.remove('hidden');
        }
        pageInfoEl.textContent = `Trang ${pagination.currentPage} / ${totalPages}`;
        prevPageBtn.disabled = pagination.currentPage === 1;
        nextPageBtn.disabled = pagination.currentPage === totalPages;
        applyToggles();
        updateSum(paginatedRows);
    }
    function renderList(rows) {
        tbody.innerHTML = '';
        rows.forEach((r, i) => {
            const tr = document.createElement('tr');
            const displayIndex = (pagination.currentPage - 1) * pagination.rowsPerPage + i + 1;
            const td0 = document.createElement('td');
            td0.dataset.col = 'select';
            const cb = document.createElement('input');
            cb.type = 'checkbox';
            cb.className = 'form-check-input';
            cb.dataset.key = r.key || r.id;
            td0.append(cb);
            tr.append(td0);
            const name = r.name || '';
            const address = r.address || '';
            const account = r.account || '';
            const prev = r.amount_previous || r.prev || '0';
            const curr = r.amount_current || r.curr || '0';
            const total = r.total || '0';
            const nhap = r.nhapat || r.nhap || '';
            const xuat = r.xuatat || r.xuat || '';
            const member = r.membername || r.memberName || '';
            const employee = r.employee_username || '';
            const cells = [
                { col: 'index', text: displayIndex, dataSort: displayIndex },
                { col: 'name', text: name },
                { col: 'address', text: address },
                { col: 'account', text: account },
                { col: 'prev', text: fmtMoney(prev), dataSort: prev },
                { col: 'curr', text: fmtMoney(curr), dataSort: curr },
                { col: 'total', text: fmtMoney(total), dataSort: total, dataTotal: total },
                { col: 'nhap', text: fmtDate(nhap), dataSort: nhap },
                { col: 'xuat', text: fmtDate(xuat), dataSort: xuat },
                { col: 'member', text: member },
                { col: 'employee', text: employee }
            ];
            cells.forEach(c => {
                const td = document.createElement('td');
                td.dataset.col = c.col;
                if (c.dataTotal != null) td.dataset.total = c.dataTotal;
                if (c.dataSort != null) td.dataset.sortval = c.dataSort;
                td.textContent = c.text;
                tr.append(td);
            });
            tbody.append(tr);
        });
        table.classList.toggle('hidden', rows.length === 0);
    }
    function renderGrid(rows) {
         if(!gridContainer) return;
        gridContainer.innerHTML = '';
        rows.forEach(r => {
            const card = document.createElement('div');
            card.className = 'card shadow-sm card-hover grid-card';
            const name = r.name || '';
            const account = r.account || '';
            const address = r.address || '';
            const total = r.total || '0';
            const prev = r.amount_previous || r.prev || '0';
            const curr = r.amount_current || r.curr || '0';
            const nhap = r.nhapat || r.nhap || '';
            const xuat = r.xuatat || r.xuat || '';
            const member = r.membername || r.memberName || '';
            const employee = r.employee_username || '';
            card.innerHTML = ` <div class="card-header fw-bold">${name}</div> <div class="card-body"> <p class="card-text">Mã KH: ${account}</p> <p class="card-text">Địa chỉ: ${address}</p> <p class="card-total">${fmtMoney(total)}</p> <p class="card-text">Kỳ trước: ${fmtMoney(prev)} | Kỳ này: ${fmtMoney(curr)}</p> <div class="card-footer bg-transparent border-top-0 px-0 pt-2"> ${nhap ? `<small class="d-block">Nhập: ${fmtDate(nhap)}</small>` : ''} ${xuat ? `<small class="d-block">Xuất: ${fmtDate(xuat)}</small>` : ''} ${member ? `<small class="d-block">KHT: ${member}</small>` : ''} ${employee ? `<small class="d-block">NV Bán: ${employee}</small>` : ''} </div> </div> `;
            gridContainer.append(card);
        });
    }


    // --- Filtering, Sorting, UI Updates ---
    const updateSum = (rows) => { if (sumTotalEl) sumTotalEl.textContent = fmtMoney(rows.reduce((s, r) => s + (parseInt(r.total, 10) || 0), 0)); };
    function applyFiltersAndSort() {
        if (!searchInput || !hideZeroBtn) return;
        const term = searchInput.value.toLowerCase();
        const hideZero = hideZeroBtn.checked;
        currentRows = allRows.filter(r => {
            if (hideZero && parseMoney(r.total) === 0) return false;
            if (term) {
                const content = [r.name, r.address, r.account, r.employee_username].join(' ').toLowerCase();
                if (!content.includes(term)) return false;
            }
            return true;
        });
        currentRows.sort((a, b) => {
            let va, vb;
            if (sortKey === 'index') { va = allRows.indexOf(a); vb = allRows.indexOf(b); }
            else if (sortKey === 'nhap') { va = a.nhapat || a.nhap; vb = b.nhapat || b.nhap; }
            else if (sortKey === 'xuat') { va = a.xuatat || a.xuat; vb = b.xuatat || b.xuat; }
            else if (sortKey === 'member') { va = a.membername || a.memberName; vb = b.membername || b.memberName; }
            else if (sortKey === 'employee') { va = a.employee_username; vb = b.employee_username; }
            else if (sortKey === 'prev') { va = a.amount_previous || r.prev; vb = b.amount_previous || b.prev; }
            else if (sortKey === 'curr') { va = a.amount_current || r.curr; vb = b.amount_current || b.curr; }
            else { va = a[sortKey]; vb = b[sortKey]; }
            let cmp = 0;
            const type = thead?.querySelector(`th[data-col="${sortKey}"]`)?.dataset.sort || 'text';
            if (type === 'money') cmp = (Number(va) || 0) - (Number(vb) || 0);
            else if (type === 'index') cmp = va - vb;
            else if (type === 'date') cmp = (va || '').localeCompare(vb || '');
            else cmp = (va || '').toString().localeCompare((vb || '').toString(), 'vi', { sensitivity: 'base' });
            return sortDir * cmp;
        });
    }
    function applyToggles() {
        if (!colToggles || !table || !tfoot) return;
        const isHistory = (viewMode === 'history');
        const isKho = (viewMode === 'kho');
        const showList = (displayMode === 'list');
        colToggles.forEach(cb => {
            const col = cb.dataset.col;
            let hide = !cb.checked;
            if (col === 'nhap' || col === 'xuat') { if (!isHistory && !isKho) hide = true; }
            if (col === 'member') { if (!isHistory) hide = true; }
            if (col === 'employee') { if (!isHistory) hide = true; }
            // Ẩn cột 'prev' và 'curr' nếu đang xem Cổng 2 (vì Cổng 2 không có)
            if (viewMode === 'lookup' && /^\d{8}$/.test(providerEl.value)) {
                 if (col === 'prev' || col === 'curr') hide = true;
            }

            table.querySelectorAll(`[data-col="${col}"]`).forEach(el => el.classList.toggle('hidden', hide || !showList));
        });
        tfoot.classList.toggle('hidden', !showList);
    }
    function sortBy(col, type) {
        if (!thead) return;
        if (sortKey === col) { sortDir *= -1; }
        else { sortDir = 1; sortKey = col; }
        thead.querySelectorAll('th').forEach(th => th.classList.remove('sorted-asc','sorted-desc'));
        if (sortKey) {
            const th = thead.querySelector(`th[data-col="${sortKey}"]`);
            if (th) th.classList.add(sortDir === 1 ? 'sorted-asc' : 'sorted-desc');
        }
        applyFiltersAndSort();
        runDisplay();
    }
    function normalizeResp(r) { const d = r.data || {}; const bills = Array.isArray(d.bills) ? d.bills : []; const curr = +d.statement_closing || +bills[0]?.amount || 0; const prev = +bills[1]?.amount || 0; const tot = +d.total_bill_amount || curr; return { key: `${r.product_id}::${d.account||r.account}`, account: d.account || r.account, name: d.name || '-', address: d.address || '-', curr: String(curr), prev: String(prev), total: String(tot), nhap: '', xuat: '', memberName: '', employee_username: '', amount_current: String(curr), amount_previous: String(prev), provider_id: r.product_id, raw: d }; }

    // ** SỬA: Cập nhật hàm normalizeResp_Port2 **
    function normalizeResp_Port2(r, accountCode) {
        const provider_id = 'C2-7ty.vn';

        // TRƯỜNG HỢP 1: API 7ty.vn trả về THÀNH CÔNG (status 200)
        // Cấu trúc data: r.success == true, r.data.success == true
        if (r.success && r.data && r.data.success && r.data.data && r.data.data.bills) {
            if (r.data.data.bills.length > 0) {
                const bill = r.data.data.bills[0];
                const name = bill.customerName || '-';
                const address = bill.address || '-';
                const total = bill.moneyAmount || '0';
                return {
                    key: `${provider_id}::${accountCode}`, account: accountCode,
                    name: name, address: address,
                    curr: String(total), prev: '0', total: String(total),
                    nhap: '', xuat: '', memberName: '', employee_username: '',
                    amount_current: String(total), amount_previous: '0',
                    provider_id: provider_id, raw: r.data.data // Lưu data.data
                };
            } else {
                // Thành công nhưng không có bill (không nợ cước)
                return {
                    key: `${provider_id}::${accountCode}`, account: accountCode,
                    name: `(Mã ${accountCode})`, address: 'Không nợ cước',
                    curr: '0', prev: '0', total: '0',
                    nhap: '', xuat: '', memberName: '', employee_username: '',
                    amount_current: '0', amount_previous: '0',
                    provider_id: provider_id, raw: r.data.data
                };
            }
        }


        // TRƯỜNG HỢP 2: API 7ty.vn trả về lỗi
        let errorMsg = r.error?.message || r.details || 'Lỗi tra cứu C2';
        if (r.data && r.data.error) {
            errorMsg = r.data.error.message || 'Lỗi không xác định C2';
        }
        if (r.error && (r.error.code === 'PAYBILL_QUERY_ERROR-01' || r.error === 'Khách hàng không nợ cước')) {
             errorMsg = "Không nợ cước";
        }
        if (r.detail && Array.isArray(r.detail) && r.detail[0]?.msg) {
            errorMsg = `Lỗi Cổng 2: ${r.detail[0].msg.replace("Field required", "Trường bắt buộc")} (trường ${r.detail[0].loc.join('.')})`;
        }




        return {
            key: `${provider_id}::${accountCode}`, account: accountCode,
            name: `(Mã ${accountCode})`, address: errorMsg,
            curr: '0', prev: '0', total: '0',
            nhap: '', xuat: '', memberName: '', employee_username: '',
            amount_current: '0', amount_previous: '0',
            provider_id: provider_id, raw: { error: errorMsg }
        };
    }




